definition.script:
pipeline {
    agent { label 'tomcat'}
    stages {
        stage('checkout') {
            steps {
                sh 'echo "testing the checkout stage of pipeline"'
                sh 'sudo mkdir -p /home/ubuntu/javaproject'
                sh 'sudo chmod -R 777 /home/ubuntu'
                dir("/home/ubuntu/javaproject") {
                        sh 'sudo rm -rf hello-world-war'
                        sh 'git clone https://github.com/sraahul/hello-world-war.git'
                }

            }
        }
        stage('build') {
            steps {
                sh 'echo "testing the build stage of pipeline"'
                dir("/home/ubuntu/javaproject/hello-world-war") {
                        sh 'pwd'
                        sh 'mvn package'
                }

            }
        }
        stage('deploy') {
            steps {
                sh 'echo "testing the deploy stage of pipeline"'
                dir("/home/ubuntu/javaproject/hello-world-war/target") {
                       sh 'sudo cp -r hello-world-war-1.0.0.war /opt/apache-tomcat-9.0.62/webapps/'
                       sh 'sudo sh /opt/apache-tomcat-9.0.62/bin/shutdown.sh'
                       sh 'sleep 5'
                       sh 'sudo sh /opt/apache-tomcat-9.0.62/bin/startup.sh'
                }

            }
        }
    }
}
